
# https://www.hackerrank.com/challenges/the-minion-game/problem

vowels = frozenset("AEIOU")


def minion_game(string):
    strl = len(string)
    kevsc = 0
    stusc = 0
    for i in range(strl):
        a = string[i]
        if a in vowels:
            kevsc += (strl - i)
        else:
            stusc += (strl - i)

    if kevsc > stusc:
        print("Kevin", kevsc)
    elif kevsc < stusc:
        print("Stuart", stusc)
    else:
        print("Draw")


if __name__ == '__main__':
    # s = input()
    # s = ''''''
    s = '''BANANA'''
    minion_game(s)